from aiogram import Router
from aiogram.enums import ParseMode
from aiogram.filters import Command
from aiogram.types import Message

import app.keyboards as kb

router = Router()


@router.message(Command(commands=['help']))
async def get_help(message: Message):
    text = '<b><i>–î–æ–±–∞–≤–ª—è–π—Ç–µ –ö–∞—Ä—Ç–æ—á–∫–∏ –õ–µ–≥–∫–æ –∏ –ë—ã—Å—Ç—Ä–æ!</i></b> üåü –ü—Ä–∏–≤–µ—Ç! üòä –ú—ã —É–ø—Ä–æ—Å—Ç–∏–ª–∏ –ø—Ä–æ—Ü–µ—Å—Å –¥–æ–±–∞–≤–ª–µ–Ω–∏—è –∫–∞—Ä—Ç–æ—á–µ–∫. –¢–µ–ø–µ—Ä—å –≤—ã –º–æ–∂–µ—Ç–µ –±—ã—Å—Ç—Ä–æ –¥–æ–±–∞–≤–∏—Ç—å <b>—Å–ª–æ–≤–æ</b> –∏–ª–∏ <b>–ø—Ä–µ–¥–ª–æ–∂–µ–Ω–∏–µ</b> –∏–∑ –ª—é–±–æ–≥–æ –ø—Ä–∏–ª–æ–∂–µ–Ω–∏—è –∏–ª–∏ –±—Ä–∞—É–∑–µ—Ä–∞. –ü—Ä–æ—Å—Ç–æ –≤—ã–¥–µ–ª–∏—Ç–µ –Ω—É–∂–Ω—ã–π —Ç–µ–∫—Å—Ç, –Ω–∞–∂–º–∏—Ç–µ ¬´–ü–æ–¥–µ–ª–∏—Ç—å—Å—è¬ª üì§ –∏ –≤—ã–±–µ—Ä–∏—Ç–µ –¢–µ–ª–µ–≥—Ä–∞–º, —á—Ç–æ–±—ã –æ—Ç–ø—Ä–∞–≤–∏—Ç—å –Ω–∞—à–µ–º—É <b>–ê–Ω–∫–∏–ë–æ—Ç—É</b> ü§ñ .\n\n‚úç –í—ã –º–æ–∂–µ—Ç–µ –∑–∞–ø–æ–ª–Ω–∏—Ç—å –ø–æ–ª–µ ¬´<b>–î–æ–±–∞–≤–∏—Ç—å –∫–æ–º–º–µ–Ω—Ç–∞—Ä–∏–π</b>¬ª, —á—Ç–æ–±—ã —Å–æ–∑–¥–∞—Ç—å –≤—Ç–æ—Ä—É—é —Å—Ç–æ—Ä–æ–Ω—É –∫–∞—Ä—Ç–æ—á–∫–∏, –∏–ª–∏ –æ—Å—Ç–∞–≤–∏—Ç—å –µ–≥–æ –ø—É—Å—Ç—ã–º –∏ –¥–æ–±–∞–≤–∏—Ç—å –≤—Ç–æ—Ä—É—é —Å—Ç–æ—Ä–æ–Ω—É –ø–æ–∑–∂–µ –≤ –æ–∫–Ω–µ <b>—á–∞—Ç–∞</b>.\n\nüß© –ö–∞–∫ —Ç–æ–ª—å–∫–æ —è –ø–æ–ª—É—á—É –æ–±–∞ —Å–æ–æ–±—â–µ–Ω–∏—è, —è –ø–æ–π–º—É, —á—Ç–æ –≤—ã —Ö–æ—Ç–∏—Ç–µ –¥–æ–±–∞–≤–∏—Ç—å –Ω–æ–≤—É—é –∫–∞—Ä—Ç–æ—á–∫—É, –∏ –ø—Ä–µ–¥–ª–æ–∂—É –≤—ã–±—Ä–∞—Ç—å –ø–æ–¥—Ö–æ–¥—è—â—É—é <b>–∫–∞—Ç–µ–≥–æ—Ä–∏—é</b> üóÇÔ∏è. –í—Å—ë <b>–±—ã—Å—Ç—Ä–æ</b> –∏ <b>–ø—Ä–æ—Å—Ç–æ</b>! –£–¥–∞—á–∏! ‚ú®'
    await message.answer(text, reply_markup=await kb.back(), parse_mode=ParseMode.HTML)
